const EventHandling = {
  data() {
    return {
      message: 'Hello Vue.js!'
    }
  },
  methods: {　//メソッドを置いておくところ。各メソッドがそれぞれ呼ばれたら、それぞれ実行される。
    reverseMessage() {
      this.message = this.message　//messageの中に入っている文字列に以下の3つの処理を上から順番にしてmessageに代入する。
        .split('') //1文字ずつの配列になる
        .reverse()//配列を逆転させる
        .join('')//配列を連結して文字列にする
    }
  }
}
