import { mapGetters, mapActions } from 'vuex'
import { UPDATE_HOLIDAY } from '../store/mutation-types'
export default {
  data() {
    return {
      weekdays: ['Sun', 'Mon', 'Tue', 'Wed', 'Tur', 'Fri', 'Sat'],
      year: 2020,
      month: 10,
      day: -1,
      today: '',
      checkHoliday: false,
      nextHolidayObj: '',
      nextHolidayName: '',
      nextHolidayDate: '',
      termUntilNextHoliday: 0
    };
  },
  computed: {
    ...mapGetters(['holidayList']),
    calData() {
      const calData = []
      const firstWeekDay = new Date(this.year, this.month - 1, 1).getDay()
      const lastDay = new Date(this.year, this.month, 0).getDate()
      let dayNum = 1
      while (dayNum <= lastDay) {
        const weekData = []
        // 日曜～土曜の日付データを配列で作成
        for (let i = 0; i <= 6; i++) {
          if (calData.length === 0 && i < firstWeekDay) {
            // 初週の1日以前の曜日は空文字
            weekData[i] = ''
          } else if (lastDay < dayNum) {
            // 最終日以降の曜日は空文字
            weekData[i] = ''
          } else {
            // 通常の日付入力
            weekData[i] = dayNum
            dayNum++
          }
        }
        calData.push(weekData)
      }
      return calData
    }
  },
  created() {
    // 祝日データ取得
