        this[UPDATE_HOLIDAY](data)
      })
  },
  mounted() {
    // 今日の日付を文字列で算出
    const date = new Date()
    const y = date.getFullYear()
    const m = ('0' + (date.getMonth() + 1)).slice(-2)
    const d = ('0' + date.getDate()).slice(-2)
    this.year = y
    this.month = Number(m)
    this.today = y + '-' + m + '-' + d
  },
  methods: {
    ...mapActions([UPDATE_HOLIDAY]),
    setLastMonth() {
      if (this.month === 1) {
        this.year--
        this.month = 12
      } else {
        this.month--
      }
      this.day = -1
    },
    setNextMonth() {
      if (this.month === 12) {
        this.year++
        this.month = 1
      } else {
        this.month++
      }
      this.day = -1
    },
    dateClick(dayNum) {
      if (dayNum !== '') {
        this.day = dayNum
      }
    },
