def setPlt(x,y,mode):

    fig, ax = plt.subplots(facecolor='white') 
    if mode =="plot":
        ax.plot(x,y)
        plt.title("X-Y plot")
    elif mode == "bar":
        ax.bar(x,y)
        plt.title("X-Y var")
    plt.xlabel("X")
    plt.ylabel("Y")
# SVG化
def plt2svg():
    buf = io.BytesIO()
    plt.savefig(buf, format='svg', bbox_inches='tight')
    s = buf.getvalue()
    buf.close()
    return s

# 実行するビュー関数
def get_svg(request):
    plusbtn=request.session['plusbtn']
    minusbtn=request.session['minusbtn']
    delta=int(request.session['delta'])
    x1=request.session['xvalue']
    y1=request.session['yvalue']

    if plusbtn == 'ON'or minusbtn == 'ON':
        x=x1.split(",")

        y2=y1.split(",")
        y = [int(n) for n in y2]
        y[1]=delta

    else:
        x=x1.split(",")
        y=y1.split(",")
    request.session['plusbtn']='OFF'
    request.session['minusbtn']='OFF'
    request.session['delta']=delta
    mode=request.session['mode']
    setPlt(x,y,mode)  
    svg = plt2svg()  #SVG化
    plt.cla()  # グラフをリセット
